mapper.select:
SELECT TOTAL_CNT, ROWNUMBER, PRJT_ID, BOPT_ID, BOPT_NM, LAST_CUST_ID, LAST_CUST_NM, PRJT_NM, ORDE_CUST_ID, ORDE_CUST_NM, PRJT_TYPE_CD, PRJT_TYPE_CD_NM, CNTR_DATE, CNTR_TRSF_START_YAM, SUM_SELL_AMT, WCT_PRBB_CD, WCT_PRBB_CD_NM, SLS_DEPT_ID, SLS_DEPT_NM, SLS_EMP_ID, SLS_EMP_NM, PRJT_STAT_CD, PRJT_STAT_CD_NM, BOPT_STAT_CD, BOPT_STAT_CD_NM, FCST_RFLC_YN, COM_CD_NUM_VAL FROM ( SELECT COUNT(1) OVER() AS TOTAL_CNT, ROWNUM AS ROWNUMBER, TF.PRJT_ID, TF.BOPT_ID, TF.BOPT_NM, TF.LAST_CUST_ID, TF.LAST_CUST_NM, TF.PRJT_NM, TF.ORDE_CUST_ID, TF.ORDE_CUST_NM, TF.PRJT_TYPE_CD, TF.PRJT_TYPE_CD_NM, TF.CNTR_DATE, TF.CNTR_TRSF_START_YAM, TF.SUM_SELL_AMT, TF.WCT_PRBB_CD, TF.WCT_PRBB_CD_NM, TF.SLS_DEPT_ID, TF.SLS_DEPT_NM, TF.SLS_EMP_ID, TF.SLS_EMP_NM, TF.PRJT_STAT_CD, TF.PRJT_STAT_CD_NM, TF.BOPT_STAT_CD, TF.BOPT_STAT_CD_NM, TF.FCST_RFLC_YN, TF.COM_CD_NUM_VAL FROM ( SELECT T1.PRJT_ID ,T1.BOPT_ID, T1.BOPT_NM ,T3.LAST_CUST_ID, IBIZ_CUST_NM(T3.LAST_CUST_ID) AS LAST_CUST_NM ,T3.PRJT_NM ,T1.ORDE_CUST_ID, IBIZ_CUST_NM(T1.ORDE_CUST_ID) AS ORDE_CUST_NM ,T3.PRJT_TYPE_CD, IBIZ_COM_CD_NM('PRJT_TYPE_CD',T3.PRJT_TYPE_CD) AS PRJT_TYPE_CD_NM ,T1.CNTR_DATE ,T1.CNTR_TRSF_START_YAM ,( SELECT SUM(SELL_AMT) FROM BPIP020T WHERE BOPT_ID = T1.BOPT_ID ) AS SUM_SELL_AMT ,T1.WCT_PRBB_CD, IBIZ_COM_CD_NM('WCT_PRBB_CD',T1.WCT_PRBB_CD) AS WCT_PRBB_CD_NM ,T1.SLS_DEPT_ID, IBIZ_DEPT_NM(T1.SLS_DEPT_ID) AS SLS_DEPT_NM ,T1.SLS_EMP_ID, IBIZ_EMP_NM(T1.SLS_EMP_ID) AS SLS_EMP_NM ,T3.PRJT_STAT_CD, IBIZ_COM_CD_NM('PRJT_STAT_CD',T3.PRJT_STAT_CD) AS PRJT_STAT_CD_NM ,T1.BOPT_STAT_CD, IBIZ_COM_CD_NM('BOPT_STAT_CD',T1.BOPT_STAT_CD) AS BOPT_STAT_CD_NM ,T1.FCST_RFLC_YN ,T2.COM_CD_NUM_VAL FROM BPIP000T T1 JOIN ACOM010T T2 ON (T1.WCT_PRBB_CD = T2.COM_CD AND T2.COM_GRP_CD = (SELECT COM_GRP_CD FROM ACOM020T WHERE CLMN_NM = 'WCT_PRBB_CD') )-- 수주확률코드 ) JOIN APRJ000T T3 ON T1.PRJT_ID = T3.PRJT_ID WHERE 1=1 AND T1.BOPT_STAT_CD != 'D1'
AND T1.PRJT_ID = UPPER(#{prjtId})
AND UPPER(T3.PRJT_NM) LIKE '%'||UPPER(#{prjtNm})||'%'
AND UPPER(T3.LAST_CUST_ID) = UPPER(#{lastCustId})
AND UPPER(IBIZ_CUST_NM(T3.LAST_CUST_ID)) LIKE '%'||UPPER(#{lastCustNm})||'%'
AND T1.BOPT_ID = UPPER(#{boptId})
AND UPPER(T1.BOPT_NM) LIKE '%'||UPPER(#{boptNm})||'%'
AND T1.SLS_DEPT_ID IN (SELECT DEPT_ID FROM HMST100T START WITH DEPT_ID = #{slsDeptId} -- ★조회조건 대상부서 CONNECT BY PRIOR DEPT_ID = HGRK_DEPT_ID)
AND T1.SLS_Emp_ID = #{slsEmpId}
AND T3.PRJT_STAT_CD = #{prjtStatCd}
AND T1.BOPT_STAT_CD = #{boptStatCd}
AND BOPT_STAT_CD = #{boptStatCd}
AND PRJT_TYPE_CD = #{prjtTypeCd}
AND FCST_RFLC_YN = #{fcstRflcYn}
AND BSNS_CLSF_CD = #{bsnsClsfCd}
AND #{cntrStartDate} <= TO_CHAR(TO_DATE(T1.CNTR_DATE,'YYYYMMDD'),'YYYYMMDD')
AND #{cntrEndDate} >= TO_CHAR(TO_DATE(T1.CNTR_DATE,'YYYYMMDD'),'YYYYMMDD')
AND EXISTS (SELECT 1 FROM BPIP020T WHERE BOPT_ID = T1.BOPT_ID AND TO_CHAR(TO_DATE(#{sellStartDate},'YYYYMMDD'),'YYYYMM') <= FCST_YAM AND SELL_AMT != 0)
AND EXISTS (SELECT 1 FROM BPIP020T WHERE BOPT_ID = T1.BOPT_ID AND TO_CHAR(TO_DATE(#{sellEndDate},'YYYYMMDD'),'YYYYMM') >= FCST_YAM AND SELL_AMT != 0)
AND EXISTS (SELECT 1 FROM BPIP020T WHERE BOPT_ID = T1.BOPT_ID AND TO_CHAR(TO_DATE(#{buyStartDate},'YYYYMMDD'),'YYYYMM') <= FCST_YAM AND BUY_AMT != 0)
AND EXISTS (SELECT 1 FROM BPIP020T WHERE BOPT_ID = T1.BOPT_ID AND TO_CHAR(TO_DATE(#{buyEndDate},'YYYYMMDD'),'YYYYMM') >= FCST_YAM AND BUY_AMT != 0)
AND BOPT_STAT_CD != 'E'
ORDER BY IBIZ_CUST_NM(T3.LAST_CUST_ID) ASC , BOPT_NM ASC, CNTR_DATE ASC
ORDER BY CNTR_DATE ASC, IBIZ_CUST_NM(T3.LAST_CUST_ID) ASC , BOPT_NM ASC
ORDER BY NVL(SUM_SELL_AMT,0) DESC , IBIZ_CUST_NM(T3.LAST_CUST_ID) ASC , BOPT_NM ASC
ORDER BY COM_CD_NUM_VAL DESC, CNTR_DATE, IBIZ_CUST_NM(T3.LAST_CUST_ID) ASC , BOPT_NM ASC
ORDER BY IBIZ_CUST_NM(T3.LAST_CUST_ID) ASC , BOPT_NM ASC, CNTR_DATE ASC
)TF ) WHERE ROWNUMBER BETWEEN #{pageSize} * (#{pageNumber} - 1) + 1 AND #{pageSize} * (#{pageNumber} - 1) + #{pageSize}

SELECT * FROM ( SELECT COUNT(1) OVER() AS TOTAL_CNT, ROWNUM AS ROWNUMBER ,T.BOPT_ID,T.BOPT_NM,T.PRJT_ID, T.PRJT_NM ,T.PRJT_TYPE_CD,T.PRJT_TYPE_CD_NM,T.LAST_CUST_ID,T.LAST_CUST_NM ,T.ORDE_CUST_ID,T.ORDE_CUST_NM,T.SLS_DEPT_ID,T.SLS_DEPT_NM ,T.SLS_EMP_ID,T.SLS_EMP_NM,T.BOPT_STAT_CD,T.BOPT_STAT_CD_NM ,T.BOPT_END_TYPE_CD,T.BOPT_END_TYPE_CD_NM ,T.CNTR_TRSF_START_YAM,T.CNTR_TRSF_END_YAM,T.CNTR_DATE FROM ( SELECT T1.BOPT_ID ,T1.BOPT_NM ,T1.PRJT_ID ,IBIZ_PRJT_NM(T1.PRJT_ID) AS PRJT_NM ,T2.PRJT_TYPE_CD ,IBIZ_COM_CD_NM('PRJT_TYPE_CD',T2.PRJT_TYPE_CD) AS PRJT_TYPE_CD_NM ,T2.LAST_CUST_ID ,IBIZ_CUST_NM(T2.LAST_CUST_ID) AS LAST_CUST_NM ,T1.ORDE_CUST_ID ,IBIZ_CUST_NM(T1.ORDE_CUST_ID) AS ORDE_CUST_NM ,T1.SLS_DEPT_ID ,IBIZ_DEPT_NM(T1.SLS_DEPT_ID) AS SLS_DEPT_NM ,T1.SLS_EMP_ID ,IBIZ_EMP_NM(T1.SLS_EMP_ID) AS SLS_EMP_NM ,T1.BOPT_STAT_CD ,IBIZ_COM_CD_NM('BOPT_STAT_CD',T1.BOPT_STAT_CD) AS BOPT_STAT_CD_NM ,T1.BOPT_END_TYPE_CD ,IBIZ_COM_CD_NM('BOPT_END_TYPE_CD',T1.BOPT_END_TYPE_CD) AS BOPT_END_TYPE_CD_NM ,T1.CNTR_TRSF_START_YAM ,T1.CNTR_TRSF_END_YAM ,T1.CNTR_DATE FROM BPIP000T T1 INNER JOIN APRJ000T T2 ON T1.PRJT_ID = T2.PRJT_ID WHERE 1=1 ) T WHERE 1=1 AND BOPT_STAT_CD != 'D1'
AND UPPER(BOPT_NM) LIKE '%'||UPPER(#{boptNm})||'%'
AND UPPER(PRJT_NM) LIKE '%'||UPPER(#{prjtNm})||'%'
AND UPPER(LAST_CUST_NM) LIKE '%'||UPPER(#{lastCustNm})||'%'
AND SLS_DEPT_ID IN ( SELECT DEPT_ID FROM HMST100T START WITH DEPT_ID = #{slsDeptId} CONNECT BY PRIOR DEPT_ID = HGRK_DEPT_ID )
AND UPPER(SLS_Emp_NM) LIKE '%'||UPPER(#{slsEmpNm})||'%'
AND BOPT_STAT_CD = #{boptStatCd}
AND BOPT_END_TYPE_CD = #{boptEndTypeCd}
AND PRJT_TYPE_CD = #{prjtTypeCd}
AND BOPT_STAT_CD != 'F' ORDER BY IBIZ_CUST_NM(T.LAST_CUST_ID) ASC , BOPT_NM ASC, CNTR_DATE ASC ) WHERE ROWNUMBER BETWEEN #{pageSize} * (#{pageNumber} - 1) + 1 AND #{pageSize} * (#{pageNumber} - 1) + #{pageSize}

mapper.update:
UPDATE BPIP000T SET
PRJT_ID = #{prjtId},
BOPT_NM = #{boptNm}, BSNS_CLSF_CD = #{bsnsClsfCd}, ORDE_CUST_ID = #{ordeCustId}, CNTR_DATE = #{cntrDate}, CNTR_TRSF_START_YAM = #{cntrTrsfStartYam}, CNTR_TRSF_END_YAM = #{cntrTrsfEndYam}, PRPS_FNSH_DATE = #{prpsFnshDate}, BID_FNSH_DATE = #{bidFnshDate}, CMPY_SLCT_DATE = #{cmpySlctDate}, WCT_PRBB_CD = #{wctPrbbCd}, FCST_RFLC_YN = #{fcstRflcYn}, DTIM_RPRT_DISP_YN = #{dtimRprtDispYn}, WCT_DCD_DATE = #{wctDcdDate}, BOPT_STAT_CD = #{boptStatCd}, BOPT_END_TYPE_CD = #{boptEndTypeCd}, SLS_DEPT_ID = #{slsDeptId}, SLS_EMP_ID = #{slsEmpId}, SPCL_CONT = #{spclCont}, CHG_EMP_ID = #{chgEmpId}, CHG_DT = TO_DATE(#{chgDt}, 'YYYY-MM-DD HH24:MI:SS') WHERE BOPT_ID = #{boptId}

mapper.select:
SELECT TOTAL_CNT, ROWNUMBER, BOPT_ID, LAST_CUST_NM, BOPT_NM, SLS_DEPT_NM, SLS_EMP_NM, CHG_DT, BEF_CNTR_YAM, BEF_CNTR_SELL_AMT, BEF_WCT_PRBB_CD_NM, BEF_BOPT_STAT_CD_NM, BEF_FCST_RFLC_YN, CNTR_YAM, CNTR_SELL_AMT, WCT_PRBB_CD_NM, BOPT_STAT_CD, BOPT_STAT_CD_NM, FCST_RFLC_YN, PRJT_ID FROM ( SELECT COUNT(1) OVER() AS TOTAL_CNT, ROWNUM AS ROWNUMBER, Z.BOPT_ID, Z.LAST_CUST_NM, Z.BOPT_NM, Z.SLS_DEPT_NM, Z.SLS_EMP_NM, Z.CHG_DT, Z.BEF_CNTR_YAM, Z.BEF_CNTR_SELL_AMT, Z.BEF_WCT_PRBB_CD_NM, Z.BEF_BOPT_STAT_CD_NM, Z.BEF_FCST_RFLC_YN, Z.CNTR_YAM, Z.CNTR_SELL_AMT, Z.WCT_PRBB_CD_NM, Z.BOPT_STAT_CD, Z.BOPT_STAT_CD_NM, Z.FCST_RFLC_YN, Z.PRJT_ID FROM( SELECT BOPT_ID, LAST_CUST_NM, BOPT_NM, SLS_DEPT_NM, SLS_EMP_NM, CHG_DT, BEF_CNTR_YAM, BEF_CNTR_SELL_AMT, BEF_WCT_PRBB_CD_NM, BEF_BOPT_STAT_CD_NM, BEF_FCST_RFLC_YN, CNTR_YAM, CNTR_SELL_AMT,WCT_PRBB_CD_NM, BOPT_STAT_CD, BOPT_STAT_CD_NM, FCST_RFLC_YN, PRJT_ID FROM BPIP001V WHERE 1 = 1
AND PRJT_ID = UPPER(#{prjtId})
AND UPPER(PRJT_NM) LIKE '%'||UPPER(#{prjtNm})||'%'
AND UPPER(LAST_CUST_ID) = UPPER(#{lastCustId})
AND UPPER(LAST_CUST_NM) LIKE '%'||UPPER(#{lastCustNm})||'%'
AND BOPT_ID = UPPER(#{boptId})
AND UPPER(BOPT_NM) LIKE '%'||UPPER(#{boptNm})||'%'
AND SLS_DEPT_ID IN (SELECT DEPT_ID FROM HMST100T START WITH DEPT_ID = #{slsDeptId} CONNECT BY PRIOR DEPT_ID = HGRK_DEPT_ID)
AND SLS_EMP_ID = #{slsEmpId}
AND PRJT_TYPE_CD = #{prjtTypeCd}
AND BSNS_CLSF_CD = #{bsnsClsfCd}
AND TO_CHAR(CHG_DT,'yyyymmdd') >= #{chgStartDate}
AND TO_CHAR(CHG_DT,'yyyymmdd') <= #{chgEndDate}
AND BEF_CNTR_YAM >= #{befCntrStartDate}
AND BEF_CNTR_YAM <= #{befCntrEndDate}
AND CNTR_YAM >= #{cntrStartDate}
AND CNTR_YAM <= #{cntrEndDate}
ORDER BY CHG_DT DESC, BOPT_ID
ORDER BY LAST_CUST_NM, BOPT_NM, BOPT_ID, CHG_DT
ORDER BY NVL(CNTR_SELL_AMT,BEF_CNTR_SELL_AMT) DESC, LAST_CUST_NM, BOPT_NM, BOPT_ID, CHG_DT DESC
ORDER BY NVL(WCT_PRBB_NUM_VAL,BEF_WCT_PRBB_NUM_VAL) DESC, LAST_CUST_NM, PRJT_NM, BOPT_ID, CHG_DT DESC
ORDER BY NVL(CNTR_YAM,BEF_CNTR_YAM) DESC, LAST_CUST_NM, BOPT_NM, BOPT_ID, CHG_DT DESC
ORDER BY CHG_DT DESC, BOPT_ID
) Z
WHERE NVL(CNTR_YAM,0) <> NVL(BEF_CNTR_YAM,0) OR NVL(CNTR_SELL_AMT,0) <> NVL(BEF_CNTR_SELL_AMT,0) OR NVL(WCT_PRBB_CD_NM,0) <> NVL(BEF_WCT_PRBB_CD_NM,0) OR NVL(BOPT_STAT_CD_NM,0) <> NVL(BEF_BOPT_STAT_CD_NM,0) OR NVL(FCST_RFLC_YN,0) <> NVL(BEF_FCST_RFLC_YN,0) ) WHERE ROWNUMBER BETWEEN #{pageSize} * (#{pageNumber} - 1) + 1 AND #{pageSize} * (#{pageNumber} - 1) + #{pageSize}

SELECT TOTAL_CNT, ROWNUMBER, BOPT_ID, LAST_CUST_NM, BOPT_NM, SLS_DEPT_NM, SLS_EMP_NM, CNTR_YAM, CNTR_SELL_AMT, BOPT_STAT_CD, BOPT_STAT_CD_NM, CHG_DT, BEF_FCST_YAM, BEF_SELL_AMT, BEF_WCT_PRBB_CD_NM, BEF_FCST_RFLC_YN, FCST_YAM, SELL_AMT, WCT_PRBB_CD_NM, FCST_RFLC_YN, PRJT_ID FROM ( SELECT COUNT(1) OVER() AS TOTAL_CNT, ROWNUM AS ROWNUMBER, Z.BOPT_ID, Z.LAST_CUST_NM, Z.BOPT_NM, Z.SLS_DEPT_NM, Z.SLS_EMP_NM, Z.CNTR_YAM, Z.CNTR_SELL_AMT, Z.BOPT_STAT_CD, Z.BOPT_STAT_CD_NM, Z.CHG_DT, Z.BEF_FCST_YAM, Z.BEF_SELL_AMT, Z.BEF_WCT_PRBB_CD_NM, Z.BEF_FCST_RFLC_YN, Z.FCST_YAM, Z.SELL_AMT, Z.WCT_PRBB_CD_NM, Z.FCST_RFLC_YN, Z.PRJT_ID FROM( SELECT BOPT_ID, LAST_CUST_NM, BOPT_NM, SLS_DEPT_NM, SLS_EMP_NM, CNTR_YAM, CNTR_SELL_AMT, BOPT_STAT_CD, BOPT_STAT_CD_NM, CHG_DT, BEF_FCST_YAM, BEF_SELL_AMT, BEF_WCT_PRBB_CD_NM, BEF_FCST_RFLC_YN, FCST_YAM, SELL_AMT, WCT_PRBB_CD_NM, FCST_RFLC_YN, PRJT_ID FROM BPIP021V WHERE 1 = 1
AND (NVL(SELL_AMT,0) <> 0 OR NVL(BEF_SELL_AMT,0) <> 0)
AND PRJT_ID = UPPER(#{prjtId})
AND UPPER(PRJT_NM) LIKE '%'||UPPER(#{prjtNm})||'%'
AND UPPER(LAST_CUST_ID) = UPPER(#{lastCustId})
AND UPPER(LAST_CUST_NM) LIKE '%'||UPPER(#{lastCustNm})||'%'
AND BOPT_ID = UPPER(#{boptId})
AND UPPER(BOPT_NM) LIKE '%'||UPPER(#{boptNm})||'%'
AND SLS_DEPT_ID IN (SELECT DEPT_ID FROM HMST100T START WITH DEPT_ID = #{slsDeptId} CONNECT BY PRIOR DEPT_ID = HGRK_DEPT_ID)
AND SLS_EMP_ID = #{slsEmpId}
AND PRJT_TYPE_CD = #{prjtTypeCd}
AND BSNS_CLSF_CD = #{bsnsClsfCd}
AND BEF_FCST_YAM >= #{befSellStartDate}
AND BEF_FCST_YAM <= #{befSellEndDate}
AND FCST_YAM >= #{sellStartDate}
AND FCST_YAM <= #{sellEndDate}
AND TO_CHAR(CHG_DT,'yyyymmdd') >= #{chgStartDate}
AND TO_CHAR(CHG_DT,'yyyymmdd') <= #{chgEndDate}
ORDER BY CHG_DT DESC, BOPT_ID, NVL(FCST_YAM,BEF_FCST_YAM)
ORDER BY LAST_CUST_NM, BOPT_NM, BOPT_ID, CHG_DT, NVL(FCST_YAM,BEF_FCST_YAM)
ORDER BY CNTR_SELL_AMT DESC, LAST_CUST_NM, BOPT_NM, BOPT_ID, CHG_DT DESC, NVL(FCST_YAM,BEF_FCST_YAM)
ORDER BY NVL(SELL_AMT,BEF_SELL_AMT) DESC, LAST_CUST_NM, BOPT_NM, BOPT_ID, CHG_DT DESC, NVL(FCST_YAM,BEF_FCST_YAM)
ORDER BY NVL(WCT_PRBB_NUM_VAL,BEF_WCT_PRBB_NUM_VAL) DESC, LAST_CUST_NM, PRJT_NM, BOPT_ID, CHG_DT DESC, NVL(FCST_YAM,BEF_FCST_YAM)
ORDER BY NVL(FCST_YAM,BEF_FCST_YAM) DESC, LAST_CUST_NM, BOPT_NM, BOPT_ID, CHG_DT DESC
ORDER BY CHG_DT DESC, BOPT_ID, NVL(FCST_YAM,BEF_FCST_YAM)
) Z
WHERE NVL(FCST_YAM,0) <> NVL(BEF_FCST_YAM,0) OR NVL(WCT_PRBB_CD_NM,0) <> NVL(BEF_WCT_PRBB_CD_NM,0) OR NVL(FCST_RFLC_YN,0) <> NVL(BEF_FCST_RFLC_YN,0) OR NVL(SELL_AMT,0) <> NVL(BEF_SELL_AMT,0) ) WHERE ROWNUMBER BETWEEN #{pageSize} * (#{pageNumber} - 1) + 1 AND #{pageSize} * (#{pageNumber} - 1) + #{pageSize}

SELECT TOTAL_CNT, ROWNUMBER, BOPT_ID, LAST_CUST_NM, BOPT_NM, SLS_DEPT_NM, SLS_EMP_NM, CNTR_YAM, CNTR_SELL_AMT, BOPT_STAT_CD, BOPT_STAT_CD_NM, CHG_DT, BEF_FCST_YAM, BEF_BUY_AMT, BEF_WCT_PRBB_CD_NM, BEF_FCST_RFLC_YN, FCST_YAM, BUY_AMT, WCT_PRBB_CD_NM, FCST_RFLC_YN, PRJT_ID FROM ( SELECT COUNT(1) OVER() AS TOTAL_CNT, ROWNUM AS ROWNUMBER, Z.BOPT_ID, Z.LAST_CUST_NM, Z.BOPT_NM, Z.SLS_DEPT_NM, Z.SLS_EMP_NM, Z.CNTR_YAM, Z.CNTR_SELL_AMT, Z.BOPT_STAT_CD, Z.BOPT_STAT_CD_NM, Z.CHG_DT, Z.BEF_FCST_YAM, Z.BEF_BUY_AMT, Z.BEF_WCT_PRBB_CD_NM, Z.BEF_FCST_RFLC_YN, Z.FCST_YAM, Z.BUY_AMT, Z.WCT_PRBB_CD_NM, Z.FCST_RFLC_YN, Z.PRJT_ID FROM( SELECT BOPT_ID, LAST_CUST_NM, BOPT_NM, SLS_DEPT_NM, SLS_EMP_NM, CNTR_YAM, CNTR_SELL_AMT, BOPT_STAT_CD, BOPT_STAT_CD_NM, CHG_DT, BEF_FCST_YAM, BEF_BUY_AMT, BEF_WCT_PRBB_CD_NM, BEF_FCST_RFLC_YN, FCST_YAM, BUY_AMT, WCT_PRBB_CD_NM, FCST_RFLC_YN, PRJT_ID FROM BPIP021V WHERE 1 = 1
AND (NVL(BUY_AMT,0) <> 0 OR NVL(BEF_BUY_AMT,0) <> 0)
AND PRJT_ID = UPPER(#{prjtId})
AND UPPER(PRJT_NM) LIKE '%'||UPPER(#{prjtNm})||'%'
AND UPPER(LAST_CUST_ID) = UPPER(#{lastCustId})
AND UPPER(LAST_CUST_NM) LIKE '%'||UPPER(#{lastCustNm})||'%'
AND BOPT_ID = UPPER(#{boptId})
AND UPPER(BOPT_NM) LIKE '%'||UPPER(#{boptNm})||'%'
AND SLS_DEPT_ID IN (SELECT DEPT_ID FROM HMST100T START WITH DEPT_ID = #{slsDeptId} CONNECT BY PRIOR DEPT_ID = HGRK_DEPT_ID)
AND SLS_EMP_ID = #{slsEmpId}
AND PRJT_TYPE_CD = #{prjtTypeCd}
AND BSNS_CLSF_CD = #{bsnsClsfCd}
AND BEF_FCST_YAM >= #{befBuyStartDate}
AND BEF_FCST_YAM <= #{befBuyEndDate}
AND FCST_YAM >= #{buyStartDate}
AND FCST_YAM <= #{buyEndDate}
AND TO_CHAR(CHG_DT,'yyyymmdd') >= #{chgStartDate}
AND TO_CHAR(CHG_DT,'yyyymmdd') <= #{chgEndDate}
ORDER BY CHG_DT DESC, BOPT_ID, NVL(FCST_YAM,BEF_FCST_YAM)
ORDER BY LAST_CUST_NM, BOPT_NM, BOPT_ID, CHG_DT, NVL(FCST_YAM,BEF_FCST_YAM)
ORDER BY CNTR_SELL_AMT DESC, LAST_CUST_NM, BOPT_NM, BOPT_ID, CHG_DT DESC, NVL(FCST_YAM,BEF_FCST_YAM)
ORDER BY NVL(BUY_AMT,BEF_BUY_AMT) DESC, LAST_CUST_NM, BOPT_NM, BOPT_ID, CHG_DT DESC, NVL(FCST_YAM,BEF_FCST_YAM)
ORDER BY NVL(WCT_PRBB_NUM_VAL,BEF_WCT_PRBB_NUM_VAL) DESC, LAST_CUST_NM, PRJT_NM, BOPT_ID, CHG_DT DESC, NVL(FCST_YAM,BEF_FCST_YAM)
ORDER BY NVL(FCST_YAM,BEF_FCST_YAM) DESC, LAST_CUST_NM, BOPT_NM, BOPT_ID, CHG_DT DESC
ORDER BY CHG_DT DESC, BOPT_ID, NVL(FCST_YAM,BEF_FCST_YAM)
) Z
WHERE NVL(FCST_YAM,0) <> NVL(BEF_FCST_YAM,0) OR NVL(WCT_PRBB_CD_NM,0) <> NVL(BEF_WCT_PRBB_CD_NM,0) OR NVL(FCST_RFLC_YN,0) <> NVL(BEF_FCST_RFLC_YN,0) OR NVL(BUY_AMT,0) <> NVL(BEF_BUY_AMT,0) ) WHERE ROWNUMBER BETWEEN #{pageSize} * (#{pageNumber} - 1) + 1 AND #{pageSize} * (#{pageNumber} - 1) + #{pageSize}

SELECT TOTAL_CNT, ROWNUMBER, BOPT_ID, LAST_CUST_NM, BOPT_NM, SLS_DEPT_NM, SLS_EMP_NM, CNTR_YAM, CNTR_SELL_AMT, BOPT_STAT_CD, BOPT_STAT_CD_NM, CHG_DT, BEF_BEXC_ROLE_CD_NM, BEF_PUT_NOP_COUNT, BEF_WCT_PRBB_CD_NM, BEF_FCST_RFLC_YN, BEXC_ROLE_CD_NM, PUT_NOP_COUNT, WCT_PRBB_CD_NM, FCST_RFLC_YN, PRJT_ID FROM ( SELECT COUNT(1) OVER() AS TOTAL_CNT, ROWNUM AS ROWNUMBER, Z.BOPT_ID, Z.LAST_CUST_NM, Z.BOPT_NM, Z.SLS_DEPT_NM, Z.SLS_EMP_NM, Z.CNTR_YAM, Z.CNTR_SELL_AMT, Z.BOPT_STAT_CD, Z.BOPT_STAT_CD_NM, Z.CHG_DT, Z.BEF_BEXC_ROLE_CD_NM, Z.BEF_PUT_NOP_COUNT, Z.BEF_WCT_PRBB_CD_NM, Z.BEF_FCST_RFLC_YN, Z.BEXC_ROLE_CD_NM, Z.PUT_NOP_COUNT, Z.WCT_PRBB_CD_NM, Z.FCST_RFLC_YN, Z.PRJT_ID FROM( SELECT BOPT_ID, LAST_CUST_NM, BOPT_NM, SLS_DEPT_NM, SLS_EMP_NM, CNTR_YAM, CNTR_SELL_AMT, BOPT_STAT_CD, BOPT_STAT_CD_NM, CHG_DT, BEF_BEXC_ROLE_CD_NM, BEF_PUT_NOP_COUNT, BEF_WCT_PRBB_CD_NM, BEF_FCST_RFLC_YN, BEXC_ROLE_CD_NM, PUT_NOP_COUNT, WCT_PRBB_CD_NM, FCST_RFLC_YN, PRJT_ID FROM BPIP031V WHERE 1 = 1
AND PRJT_ID = UPPER(#{prjtId})
AND UPPER(PRJT_NM) LIKE '%'||UPPER(#{prjtNm})||'%'
AND UPPER(LAST_CUST_ID) = UPPER(#{lastCustId})
AND UPPER(LAST_CUST_NM) LIKE '%'||UPPER(#{lastCustNm})||'%'
AND BOPT_ID = UPPER(#{boptId})
AND UPPER(BOPT_NM) LIKE '%'||UPPER(#{boptNm})||'%'
AND SLS_DEPT_ID IN (SELECT DEPT_ID FROM HMST100T START WITH DEPT_ID = #{slsDeptId} CONNECT BY PRIOR DEPT_ID = HGRK_DEPT_ID)
AND SLS_EMP_ID = #{slsEmpId}
AND PRJT_TYPE_CD = #{prjtTypeCd}
AND BSNS_CLSF_CD = #{bsnsClsfCd}
AND BEF_CNTR_YAM >= #{befCntrStartDate}
AND BEF_CNTR_YAM <= #{befCntrEndDate}
AND CNTR_YAM >= #{cntrStartDate}
AND CNTR_YAM <= #{cntrEndDate}
AND TO_CHAR(CHG_DT,'yyyymmdd') >= #{chgStartDate}
AND TO_CHAR(CHG_DT,'yyyymmdd') <= #{chgEndDate}
ORDER BY CHG_DT DESC, BOPT_ID, NVL(BEXC_ROLE_CD,BEF_BEXC_ROLE_CD)
ORDER BY LAST_CUST_NM, BOPT_NM, BOPT_ID, CHG_DT, NVL(BEXC_ROLE_CD,BEF_BEXC_ROLE_CD)
ORDER BY NVL(CNTR_SELL_AMT,BEF_CNTR_SELL_AMT) DESC, LAST_CUST_NM, BOPT_NM, BOPT_ID, CHG_DT DESC, NVL(BEXC_ROLE_CD,BEF_BEXC_ROLE_CD)
ORDER BY NVL(WCT_PRBB_NUM_VAL,BEF_WCT_PRBB_NUM_VAL) DESC, LAST_CUST_NM, PRJT_NM, BOPT_ID, CHG_DT DESC, NVL(BEXC_ROLE_CD,BEF_BEXC_ROLE_CD)
ORDER BY NVL(PUT_NOP_COUNT,BEF_PUT_NOP_COUNT) DESC, LAST_CUST_NM, BOPT_NM, BOPT_ID, CHG_DT DESC
ORDER BY NVL(CNTR_YAM,BEF_CNTR_YAM) DESC, LAST_CUST_NM, BOPT_NM, BOPT_ID, CHG_DT DESC
ORDER BY CHG_DT DESC, BOPT_ID, NVL(BEXC_ROLE_CD,BEF_BEXC_ROLE_CD)
) Z
WHERE NVL(BEXC_ROLE_CD_NM,0) <> NVL(BEF_BEXC_ROLE_CD_NM,0) OR NVL(PUT_NOP_COUNT,0) <> NVL(BEF_PUT_NOP_COUNT,0) OR NVL(WCT_PRBB_CD_NM,0) <> NVL(BEF_WCT_PRBB_CD_NM,0) OR NVL(FCST_RFLC_YN,0) <> NVL(BEF_FCST_RFLC_YN,0) ) WHERE ROWNUMBER BETWEEN #{pageSize} * (#{pageNumber} - 1) + 1 AND #{pageSize} * (#{pageNumber} - 1) + #{pageSize}

SELECT ROWNUMBER, TOTAL_CNT, PRJT_ID, SLS_DEPT_ID, SLS_EMP_ID ,BOPT_ID, LAST_CUST_NM ,BOPT_NM, ORDE_CUST_NM ,CNTR_DATE, SUM_SELL_AMT ,SLS_DEPT_NM, SLS_EMP_NM ,BOPT_STAT_CD_NM FROM ( SELECT ROWNUM AS ROWNUMBER, Z.TOTAL_CNT, Z.PRJT_ID, Z.SLS_DEPT_ID, Z.SLS_EMP_ID ,Z.BOPT_ID, Z.LAST_CUST_NM ,Z.BOPT_NM, Z.ORDE_CUST_NM ,Z.CNTR_DATE, Z.SUM_SELL_AMT ,Z.SLS_DEPT_NM, Z.SLS_EMP_NM ,Z.BOPT_STAT_CD_NM FROM ( SELECT COUNT(1) OVER() AS TOTAL_CNT, T1.PRJT_ID, T1.SLS_DEPT_ID, T1.SLS_EMP_ID ,T1.BOPT_ID ,IBIZ_CUST_NM(T3.LAST_CUST_ID) AS LAST_CUST_NM ,T1.BOPT_NM ,IBIZ_CUST_NM(T1.ORDE_CUST_ID) AS ORDE_CUST_NM ,T1.CNTR_DATE ,( SELECT SUM(SELL_AMT) FROM BPIP020T WHERE BOPT_ID = T1.BOPT_ID ) AS SUM_SELL_AMT ,IBIZ_DEPT_NM(T1.SLS_DEPT_ID) AS SLS_DEPT_NM ,IBIZ_EMP_NM(T1.SLS_EMP_ID) AS SLS_EMP_NM ,IBIZ_COM_CD_NM('BOPT_STAT_CD',T1.BOPT_STAT_CD) AS BOPT_STAT_CD_NM FROM BPIP000T T1 JOIN ACOM010T T2 ON (T1.WCT_PRBB_CD = T2.COM_CD AND T2.COM_GRP_CD = (SELECT COM_GRP_CD FROM ACOM020T WHERE CLMN_NM = 'WCT_PRBB_CD') ) JOIN APRJ000T T3 ON T1.PRJT_ID = T3.PRJT_ID WHERE 1=1 AND T1.BOPT_STAT_CD != 'D1'
AND UPPER(IBIZ_CUST_NM(T3.LAST_CUST_ID)) LIKE '%'||UPPER(#{lastCustNm})||'%'
AND UPPER(T1.BOPT_NM) LIKE '%'||UPPER(#{boptNm})||'%'
AND T1.SLS_Emp_ID = #{slsEmpId}
AND T1.SLS_DEPT_ID = #{slsDeptId}
ORDER BY IBIZ_CUST_NM(T3.LAST_CUST_ID) ASC , BOPT_NM ASC )Z ) WHERE ROWNUMBER BETWEEN #{pageSize} * (#{pageNumber} - 1) + 1 AND #{pageSize} * (#{pageNumber} - 1) + #{pageSize}
