mapper.select:
SELECT TOTAL_CNT, ROWNUMBER, DOC_NO,ESTI_ID,FCST_PAL_ID,ISU_DT,ESTI_WRT_DATE ,REG_DT,CHG_DT,LAST_CUST_ID,LAST_CUST_NM,PRJT_NM, BOPT_NM,ORDE_CUST_ID,ORDE_CUST_NM,PRJT_TYPE_CD_NM ,PRJT_TYPE_CD,SELL_AMT,SLS_DEPT_NM ,SLS_EMP_NM,ESTIS_STAT_CD_NM,ESTIS_STAT_CD FROM ( SELECT COUNT(1) OVER() AS TOTAL_CNT, ROWNUM AS ROWNUMBER, Z.DOC_NO,Z.ESTI_ID,Z.FCST_PAL_ID,Z.ISU_DT,Z.ESTI_WRT_DATE ,Z.REG_DT,Z.CHG_DT,Z.LAST_CUST_ID,Z.LAST_CUST_NM,Z.PRJT_NM ,Z.BOPT_NM,Z.ORDE_CUST_ID,Z.ORDE_CUST_NM,Z.PRJT_TYPE_CD_NM ,Z.PRJT_TYPE_CD,Z.SELL_AMT,Z.SLS_DEPT_NM ,Z.SLS_EMP_NM,Z.ESTIS_STAT_CD_NM,Z.ESTIS_STAT_CD FROM( SELECT DISTINCT T1.DOC_NO ,T1.ESTI_ID ,T1.FCST_PAL_ID ,T1.ISU_DT ,T1.ESTI_WRT_DATE ,T1.REG_DT ,T1.CHG_DT ,T2.LAST_CUST_ID ,IBIZ_CUST_NM(T2.LAST_CUST_ID)AS LAST_CUST_NM ,IBIZ_PRJT_NM(T2.PRJT_ID) AS PRJT_NM ,( SELECT BOPT_NM FROM BPIP000T WHERE BOPT_ID = T2.BOPT_ID AND BOPT_STAT_CD != 'D1' ) AS BOPT_NM ,T2.ORDE_CUST_ID ,IBIZ_CUST_NM(T2.ORDE_CUST_ID)AS ORDE_CUST_NM ,IBIZ_COM_CD_NM('PRJT_TYPE_CD',T2.PRJT_TYPE_CD) AS PRJT_TYPE_CD_NM ,T2.PRJT_TYPE_CD ,T2.SELL_AMT ,IBIZ_DEPT_NM(T2.SLS_DEPT_ID) AS SLS_DEPT_NM ,IBIZ_EMP_NM(T2.SLS_EMP_ID)AS SLS_EMP_NM ,IBIZ_COM_CD_NM('ESTIS_STAT_CD',T1.ESTIS_STAT_CD) AS ESTIS_STAT_CD_NM ,T1.ESTIS_STAT_CD FROM BEST100T T1 INNER JOIN BEST000T T2 ON T1.FCST_PAL_ID = T2.FCST_PAL_ID WHERE 1=1
AND UPPER(T1.DOC_NO) = UPPER(#{docNo})
AND UPPER(T2.PRJT_ID) = UPPER(#{prjtId})
AND UPPER(IBIZ_PRJT_NM(T2.PRJT_ID)) LIKE '%'||UPPER(#{prjtNm})||'%'
AND UPPER(T2.LAST_CUST_ID) = UPPER(#{lastCustId})
AND UPPER(IBIZ_CUST_NM(T2.LAST_CUST_ID)) LIKE '%'||UPPER(#{lastCustNm})||'%'
AND UPPER(T2.ORDE_CUST_ID) = UPPER(#{ordeCustId})
AND UPPER(IBIZ_CUST_NM(T2.ORDE_CUST_ID)) LIKE '%'||UPPER(#{ordeCustNm})||'%'
AND T2.SLS_DEPT_ID IN (SELECT DEPT_ID FROM HMST100T START WITH DEPT_ID = #{slsDeptId} CONNECT BY PRIOR DEPT_ID = HGRK_DEPT_ID)
AND T2.SLS_EMP_ID = #{slsEmpId}
AND T2.PRJT_TYPE_CD = #{prjtTypeCd}
AND T1.ESTIS_STAT_CD = #{estisStatCd}
AND TO_CHAR(T1.REG_DT, 'YYYYMMDD') >= #{fromRegDt}
AND TO_CHAR(T1.REG_DT, 'YYYYMMDD') <= #{toRegDt}
ORDER BY T1.REG_DT DESC ) Z ) WHERE ROWNUMBER BETWEEN #{pageSize} * (#{pageNumber} - 1) + 1 AND #{pageSize} * (#{pageNumber} - 1) + #{pageSize}

	SELECT PRJT_TYPE_CD
    FROM BEST000T
    WHERE FCST_PAL_ID = #{fcstPalId}

	SELECT FCST_PAL_PRGS_STAT_CD
    FROM BEST000T
    WHERE FCST_PAL_ID = #{fcstPalId}

SELECT COUNT(1)
FROM BEST100T
WHERE ESTI_ID = #{estiId}

SELECT ESTIS_STAT_CD, ( SELECT T010.COM_CD_NM FROM ( SELECT T000.COM_GRP_CD FROM ACOM000T T000 INNER JOIN ACOM020T T020 ON T000.COM_GRP_CD = T020.COM_GRP_CD WHERE
1 = 1 AND T020.CLMN_NM = 'ESTIS_STAT_CD'
) T000_T020 INNER JOIN ACOM010T T010 ON T000_T020.COM_GRP_CD = T010.COM_GRP_CD WHERE T010.USE_YN = 'Y' AND T010.COM_CD = ESTIS_STAT_CD ) AS ESTIS_STAT_CD_NM FROM BEST100T WHERE ESTI_ID = #{estiId}
