mapper.select:
SELECT PRJT_ID ,PRJT_NM ,LAST_CUST_ID, IBIZ_CUST_NM(LAST_CUST_ID) AS LAST_CUST_NM ,BSNS_DOI_CD, IBIZ_COM_CD_NM('BSNS_DOI_CD', BSNS_DOI_CD) AS BSNS_DOI_NM -- 사업중요도 ,BEXC_START_DATE, BEXC_END_DATE -- 사업수행(시작/종료)일자 ,SLS_EMP_ID, IBIZ_EMP_NM(SLS_EMP_ID) AS SLS_EMP_NM ,SLS_DEPT_ID, IBIZ_DEPT_NM(SLS_DEPT_ID) AS SLS_DEPT_NM ,PRJT_TYPE_CD, IBIZ_COM_CD_NM('PRJT_TYPE_CD', PRJT_TYPE_CD) AS PRJT_TYPE_CD_NM ,PRJT_STAT_CD, IBIZ_COM_CD_NM('PRJT_STAT_CD', PRJT_STAT_CD) AS PRJT_STAT_CD_NM ,MBSN_CUST_ID, IBIZ_CUST_NM(MBSN_CUST_ID) AS MBSN_CUST_NM -- 주사업자고객 ,BSNS_BDGT_AMT -- 사업예산금액 ,PUAH_BCKG_PPOSE_CONT, PRMRY_BSNS_CONT, THOF_BSNS_FLD_CONT -- 추진배경목적내용, 주요사업내용, 당사사업영역내용 FROM APRJ000T WHERE PRJT_ID =
( SELECT PRJT_ID FROM BPIP100T WHERE VRB_ANLY_ID = #{vrbAnlyId} )
#{prjtId}

SELECT TOTAL_CNT, ROWNUMBER, VRB_ANLY_ID, SANT_ID, LAST_CUST_ID, LAST_CUST_NM, PRJT_ID, PRJT_NM, DOC_NO, SPLY_AMT, NSLE_AMT, CTMG_AMT, PUT_NOP_COUNT, PUT_TIME_UNIT_CD, PUT_TIME_UNIT_CD_NM, SLS_DEPT_ID, SLS_DEPT_NM, SLS_EMP_ID, SLS_EMP_NM, REG_DT, EFCM_DATE, VRB_UDWG_RST_CD, VRB_UDWG_RST_CD_NM, VRB_PRGS_STAT_CD, VRB_PRGS_STAT_CD_NM, CHG_EMP_ID, CHG_EMP_NM, CHG_DT FROM (SELECT COUNT(1) OVER() AS TOTAL_CNT,ROWNUM AS ROWNUMBER, TEMP.VRB_ANLY_ID, TEMP.SANT_ID, TEMP.LAST_CUST_ID, TEMP.LAST_CUST_NM, TEMP.PRJT_ID, TEMP.PRJT_NM, TEMP.DOC_NO, TEMP.SPLY_AMT, TEMP.NSLE_AMT, TEMP.CTMG_AMT, TEMP.PUT_NOP_COUNT, TEMP.PUT_TIME_UNIT_CD, TEMP.PUT_TIME_UNIT_CD_NM, TEMP.SLS_DEPT_ID, TEMP.SLS_DEPT_NM, TEMP.SLS_EMP_ID, TEMP.SLS_EMP_NM, TEMP.REG_DT, TEMP.EFCM_DATE, TEMP.VRB_UDWG_RST_CD, TEMP.VRB_UDWG_RST_CD_NM, TEMP.VRB_PRGS_STAT_CD, TEMP.VRB_PRGS_STAT_CD_NM, TEMP.CHG_EMP_ID, TEMP.CHG_EMP_NM, TEMP.CHG_DT FROM (SELECT BPIP100T.VRB_ANLY_ID, BPIP100T.SANT_ID, APRJ000T.LAST_CUST_ID, IBIZ_CUST_NM(APRJ000T.LAST_CUST_ID) AS LAST_CUST_NM, BPIP100T.PRJT_ID, APRJ000T.PRJT_NM, BPIP100T.DOC_NO, BPIP141T.SELL_AMT AS SPLY_AMT, (BPIP141T.SELL_AMT - BPIP141T.BUY_COST_AMT) AS NSLE_AMT, BPIP141T.CTMG_AMT, BPIP141T.PUT_NOP_COUNT, BPIP141T.PUT_TIME_UNIT_CD, IBIZ_COM_CD_NM('PUT_TIME_UNIT_CD', BPIP141T.PUT_TIME_UNIT_CD) AS PUT_TIME_UNIT_CD_NM, BPIP100T.SLS_DEPT_ID, IBIZ_DEPT_NM(BPIP100T.SLS_DEPT_ID) AS SLS_DEPT_NM, BPIP100T.SLS_EMP_ID, IBIZ_EMP_NM(BPIP100T.SLS_EMP_ID) AS SLS_EMP_NM, BPIP100T.REG_DT, BPIP100T.EFCM_DATE, BPIP100T.VRB_UDWG_RST_CD, IBIZ_COM_CD_NM('VRB_UDWG_RST_CD', BPIP100T.VRB_UDWG_RST_CD) AS VRB_UDWG_RST_CD_NM, BPIP100T.VRB_PRGS_STAT_CD, IBIZ_COM_CD_NM('VRB_PRGS_STAT_CD', BPIP100T.VRB_PRGS_STAT_CD) AS VRB_PRGS_STAT_CD_NM, BPIP100T.CHG_EMP_ID, IBIZ_EMP_NM(BPIP100T.CHG_EMP_ID) AS CHG_EMP_NM, BPIP100T.CHG_DT FROM BPIP100T INNER JOIN AAPR100T AAPR ON BPIP100T.SANT_ID = AAPR.SANT_ID LEFT JOIN (SELECT VRB_ANLY_ID,'M' AS PUT_TIME_UNIT_CD, SUM(CASE WHEN PUT_TIME_UNIT_CD = 'M' THEN PUT_NOP_COUNT WHEN PUT_TIME_UNIT_CD = 'D' THEN PUT_NOP_COUNT/20 WHEN PUT_TIME_UNIT_CD = 'H' THEN PUT_NOP_COUNT/160 END) AS PUT_NOP_COUNT, SUM(CTMG_AMT) AS CTMG_AMT, NVL(SUM(SELL_AMT), 0) AS SELL_AMT, NVL(SUM(BUY_COST_AMT), 0) AS BUY_COST_AMT FROM BPIP141T GROUP BY VRB_ANLY_ID ) BPIP141T ON BPIP100T.VRB_ANLY_ID = BPIP141T.VRB_ANLY_ID INNER JOIN APRJ000T ON BPIP100T.PRJT_ID = APRJ000T.PRJT_ID WHERE BPIP100T.SLS_DEPT_ID IN (SELECT DEPT_ID FROM HMST100T START WITH HGRK_DEPT_ID IN (SELECT AUTH_DEPT_ID FROM SSYS252T WHERE USER_ID = #{sessEmpId}) CONNECT BY PRIOR DEPT_ID = HGRK_DEPT_ID UNION ALL SELECT AUTH_DEPT_ID FROM SSYS252T WHERE USER_ID = #{sessEmpId} ) ORDER BY BPIP100T.REG_DT DESC ) TEMP WHERE 1=1
AND UPPER(DOC_NO) = UPPER(#{docNo})
AND UPPER(LAST_CUST_ID) = UPPER(#{lastCustId})
AND UPPER(LAST_CUST_NM) LIKE '%'||UPPER(#{lastCustNm})||'%'
AND UPPER(PRJT_ID) = UPPER(#{prjtId})
AND UPPER(PRJT_NM) LIKE '%'||UPPER(#{prjtNm})||'%'
AND TO_CHAR(REG_DT, 'YYYYMMDD') >= #{bexcStartDate}
AND TO_CHAR(REG_DT, 'YYYYMMDD') <= #{bexcEndDate}
AND SLS_DEPT_ID IN (SELECT DEPT_ID FROM HMST100T START WITH HGRK_DEPT_ID = #{slsDeptId} CONNECT BY PRIOR DEPT_ID = HGRK_DEPT_ID UNION ALL SELECT #{slsDeptId} FROM DUAL )
AND SLS_EMP_ID = #{slsEmpId}
AND VRB_PRGS_STAT_CD = #{vrbPrgsStatCd}
) WHERE ROWNUMBER BETWEEN #{pageSize} * (#{pageNumber} - 1) + 1 AND #{pageSize} * (#{pageNumber} - 1) + #{pageSize}

			SELECT
	        CASE WHEN (
	                SELECT MAX(DOC_NO)
		            FROM BPIP100T BPIP
			        JOIN AAPR100T AAPR ON BPIP.SANT_ID = AAPR.SANT_ID
					WHERE AAPR.SANT_FRMT_CD = #{santFrmtCd}
	        ) IS NULL THEN
	         'IZ-' || ( SELECT COM_CD_CHAR_VAL
	                  	FROM ACOM010T
						WHERE COM_GRP_CD = ( SELECT COM_GRP_CD FROM ACOM020T WHERE CLMN_NM = 'SANT_FRMT_CD')
	                        AND COM_CD = #{santFrmtCd}
	                )  || '-' || TO_CHAR(SYSDATE,'yyyymmdd') ||  '-' || '00'
	        ELSE (
	                SELECT MAX(DOC_NO)
		            FROM BPIP100T BPIP
					JOIN AAPR100T AAPR ON BPIP.SANT_ID = AAPR.SANT_ID
					WHERE AAPR.SANT_FRMT_CD = #{santFrmtCd}
	        )
	        END AS DOC_NO
        FROM DUAL

mapper.insert:
INSERT INTO BPIP100T (
	VRB_ANLY_ID
	,PRJT_ID
	,FRMT_CD
	,DOC_NO
	,DOC_TITL

		,EFCM_DATE


		,VRB_UDWG_RST_CD


		,CUST_DMD_MTR


		,BSNS_CHRC_CONT


		,COPE_SCHM_CONT



		,CMPY_SLCT_WAY_CONT


		,CMPY_EVAL_WAY_CONT


		,PRTC_CMPY_PRSCD_CONT



		,SLS_DEPT_ID


		,SLS_EMP_ID

	,VRB_PRGS_STAT_CD

		,FILE_ATTC_ID

	,REG_EMP_ID
	,REG_DT
	,SANT_ID
	,CHG_EMP_ID
	,CHG_DT
) VALUES (
	#{vrbAnlyId}
	,#{prjtId}
	,#{frmtCd}
	,(SELECT IBIZ_NEW_DOC_NO('BPIP100T','DOC_NO','FRMT_CD',#{frmtCd}) FROM DUAL )
	,#{docTitl}

		,#{efcmDate}


		,#{vrbUdwgRstCd}


		,#{custDmdMtr}


		,#{bsnsChrcCont}


		,#{copeSchmCont}



		,#{cmpySlctWayCont}


		,#{cmpyEvalWayCont}


		,#{prtcCmpyPrscdCont}



		,#{slsDeptId}


		,#{slsEmpId}

	,#{vrbPrgsStatCd}

		,#{fileAttcId}

	,#{regEmpId}
	,SYSDATE
	,#{santId}
	,#{regEmpId}
	,SYSDATE
)

INSERT INTO BPIP130T (
	VRB_ANLY_ID,
	SEQ,
	CMPY_NM

		, STRH_CONT


		, WKNS_CONT


		, CPTT_GODS_NM


		, GODS_CPTT_SUP_CD


		, PRC_CPTT_SUP_CD

) VALUES (
	#{vrbAnlyId},
	(
	SELECT
	CASE
	WHEN MAX(BPIP130T.SEQ) IS NOT NULL THEN MAX(BPIP130T.SEQ) + 1
	ELSE 1
	END
	FROM
	BPIP130T
	WHERE
	VRB_ANLY_ID = #{vrbAnlyId}

	),
	#{cmpyNm}

		, #{strhCont}


		, #{wknsCont}


		, #{cpttGodsNm}


		, #{godsCpttSupCd}


		, #{prcCpttSupCd}

)

INSERT INTO BPIP170T ( VRB_ANLY_ID, SEQ, ORG_DST_CD, RVWR_DEPT_ID, RVWR_EMP_ID
, RVWR_RESO_CD
, RVWR_OFPS_CD
, RVW_OPIN_CONT
) VALUES ( #{vrbAnlyId}, ( SELECT CASE WHEN MAX(BPIP170T.SEQ) IS NOT NULL THEN MAX(BPIP170T.SEQ) + 1 ELSE 1 END FROM BPIP170T WHERE VRB_ANLY_ID = #{vrbAnlyId} ), #{orgDstCd}, #{rvwrDeptId}, #{rvwrEmpId}
, #{rvwrResoCd}
, #{rvwrOfpsCd}
, #{rvwOpinCont}
)

